import { signInWithEmailAndPassword } from "firebase/auth";
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { auth } from '../../../firebase';
import {
  MDBBtn,
  MDBContainer,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBRow,
  MDBCol,
  MDBInput,
  MDBCheckbox
}
from 'mdb-react-ui-kit';
import { Link, Redirect } from "react-router-dom";

import swal from 'sweetalert';
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory();

  // const signIn = (e) => {
  //   e.preventDefault();
  //   signInWithEmailAndPassword(auth, email, password)
  //   .then((userCredential) => {
  //     swal("SUCCESS!", "Succesfully Logged In!", "success");
  //     //console.log('User logged in!');


  //     // let getRandomString = (length) => {
  //     //     var randomChars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
  //     //     var result = '';
  //     //     for ( var i = 0; i < length; i++ ) {
  //     //         result += randomChars.charAt(Math.floor(Math.random() * randomChars.length));
  //     //     }
  //     //     return result;
  //     // }
  //     // let val = getRandomString(20);

  //     // window.localStorage.setItem('user', JSON.stringify(val));
  //   })
  //   .catch((error) => {
  //     let errorCode = error.code.split("auth/")[1];
	// 		const errorMessage = error.message;

	// 		swal("ERROR!", `${errorCode}`, "error");
  // });
  // }
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await auth.signInWithEmailAndPassword(email, password);
      swal("SUCCESS!", "Succesfully Logged In!", "success");
      //console.log('User logged in!');

      history.push('/dashboard'); // Redirect to dashboard after successful login
    } catch (error) {
      let errorCode = error.code.split("auth/")[1];
			const errorMessage = error.message;

			swal("ERROR!", `${errorCode}`, "error");
    }
  };

  return (
    <MDBContainer className='my-5 contain'>
      <MDBCard>

        <MDBRow className='g-0 d-flex align-items-center'>

          <MDBCol md='4'>
            <MDBCardImage src='https://mdbootstrap.com/img/new/ecommerce/vertical/004.jpg' alt='phone' className='rounded-t-5 rounded-tr-lg-0' fluid />
          </MDBCol>

          <MDBCol md='8'>

            <MDBCardBody>
              <form onSubmit={signIn}>
                <MDBInput 
                  wrapperClass='mb-4 field' 
                  label='Email address' 
                  id='form1'
                  type='email' 
                  value={ email } 
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
                <MDBInput 
                  wrapperClass='mb-4 field' 
                  label='Password' 
                  id='form2' 
                  type='password' 
                  value={ password }
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />

                <div className="d-flex justify-content-between mx-4 mb-4">
                  <MDBCheckbox name='flexCheck' value='' id='flexCheckDefault' label='Remember me' />
                  <a href="!#">Forgot password?</a>
                </div>

                <MDBBtn className="mb-4 w-100 button">Sign in</MDBBtn>
              </form>

              <div className="d-flex flex-row align-items-center justify-content-center pb-4 mb-4">
                <p className="mb-0">Don't have an account?</p>
                <Link to={`signup`} class="link-info">
                  <MDBBtn outline className='mx-2' color='danger'>
                    CREATE NEW
                  </MDBBtn>
                </Link>
              </div>

            </MDBCardBody>

          </MDBCol>

        </MDBRow>

      </MDBCard>
    </MDBContainer>
  );
}

export default Login;
